mapper.update:
UPDATE
    batch_job_request
SET
    polling_status = #{batchJobRequest.pollingStatus},
    job_execution_id = #{batchJobRequest.jobExecutionId},
    update_date = #{batchJobRequest.updateDate}
WHERE
    job_seq_id = #{batchJobRequest.jobSeqId}
AND
    polling_status = #{pollingStatus}
